/** preprocessor.c* 전처리기 예제* #define(재정의)*/#include <stdio.h>// define은 대문자를 쓴다.// 상수(변하는 않는 값, 식별)#define PI 3.141592ffloat circle_around(float r);float circle_width(float r);void main(){	float pi = 3.141592f;	printf("pi(%f)\n", pi);	printf("pi(%f)\n", PI);	printf("원의넓이: 반지름(%f), 넓이(%f)\n", 3.0f, circle_around(3.0f));	printf("원의둘레: 반지름(%f), 둘레(%f)\n", 3.0f, circle_width(3.0f));}/** 원의 넓이*/float circle_around(float r){	return PI * (r * r);}/** 원의 둘레*/float circle_width(float r){	return 2 * PI * r;}
